"use client";

import { useEffect, useState } from "react";
import {
  CommandDialog,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import { Button } from "@/components/ui/button";
import {
  LayoutDashboard,
  Users,
  FolderKanban,
  CheckSquare,
  Calendar,
  MessageSquare,
  FileText,
  Settings,
  Bell,
  Palette,
  BarChart3,
  TrendingUp,
  PieChart,
  Target,
  Zap,
  Clock,
  HelpCircle,
  Keyboard,
  LogOut,
} from "lucide-react";

const workspaceItems = [
  { icon: LayoutDashboard, label: "Dashboard" },
  { icon: FolderKanban, label: "Projects" },
  { icon: CheckSquare, label: "Tasks" },
  { icon: Calendar, label: "Calendar" },
  { icon: Users, label: "Team members" },
  { icon: MessageSquare, label: "Messages" },
  { icon: FileText, label: "Documents" },
  { icon: Bell, label: "Notifications" },
  { icon: Clock, label: "Time tracking" },
  { icon: Target, label: "Goals" },
];

const analyticsItems = [
  { icon: BarChart3, label: "Overview" },
  { icon: TrendingUp, label: "Performance" },
  { icon: PieChart, label: "Reports" },
  { icon: Zap, label: "Insights" },
];

const settingsItems = [
  { icon: Settings, label: "Preferences" },
  { icon: Palette, label: "Appearance" },
  { icon: Keyboard, label: "Keyboard shortcuts" },
  { icon: HelpCircle, label: "Help & support" },
  { icon: LogOut, label: "Sign out" },
];

export default function CommandMenu01() {
  const [open, setOpen] = useState(true);

  useEffect(() => {
    const down = (e: KeyboardEvent) => {
      if (e.key === "k" && (e.metaKey || e.ctrlKey)) {
        e.preventDefault();
        setOpen((open) => !open);
      }
    };

    document.addEventListener("keydown", down);
    return () => document.removeEventListener("keydown", down);
  }, []);

  return (
    <>
      <Button variant="outline" onClick={() => setOpen(true)}>
        Open Command Menu
      </Button>

      <CommandDialog open={open} onOpenChange={setOpen} showCloseButton={false}>
        <CommandInput
          placeholder="Type a command or search..."
          className="h-12"
        />
        <CommandList className="h-[320px] max-h-[320px]">
          <CommandEmpty>No results found.</CommandEmpty>
          <CommandGroup heading="Workspace">
            {workspaceItems.map((item) => (
              <CommandItem key={item.label}>
                <item.icon className="mr-2 h-4 w-4" />
                <span>{item.label}</span>
              </CommandItem>
            ))}
          </CommandGroup>
          <CommandGroup heading="Analytics">
            {analyticsItems.map((item) => (
              <CommandItem key={item.label}>
                <item.icon className="mr-2 h-4 w-4" />
                <span>{item.label}</span>
              </CommandItem>
            ))}
          </CommandGroup>
          <CommandGroup heading="Settings">
            {settingsItems.map((item) => (
              <CommandItem key={item.label}>
                <item.icon className="mr-2 h-4 w-4" />
                <span>{item.label}</span>
              </CommandItem>
            ))}
          </CommandGroup>
        </CommandList>
        <div className="flex h-12 items-center justify-end border-t px-3">
          <button
            onClick={() => setOpen(false)}
            className="flex items-center gap-1 text-sm text-muted-foreground hover:text-foreground"
          >
            <span>Close</span>
            <kbd className="pointer-events-none ml-1 inline-flex h-5 select-none items-center gap-1 rounded border bg-muted px-1.5 font-mono text-[10px] font-medium text-muted-foreground">
              Esc
            </kbd>
          </button>
        </div>
      </CommandDialog>
    </>
  );
}
